\begin{align*}
\begin{split}
g(i,j)= \left \{
\begin{array}{ll}
\alpha f(i,j),&0\le f(i,j) < a\\
\beta (f(i,j)-a)+g_{a},&a\le f(i,j) < b\\
\gamma  (f(i,j)-b)+g_{b},&b\le f(i,j) < 255
\end{array}
\right.
\end{split}
\end{align*}

