mp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;23&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;24&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;	&lt;span class=&#34;token keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;25&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include&amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N = 100010;

struct Node&amp;#123;
	int id, w;
&amp;#125;;

vector&amp;lt;Node&amp;gt; m[N];
int dist[N];
int n;

void dfs(int idx, int root, int dst)&amp;#123;
	dist[idx] = dst;
	for(auto node : m[idx])
		if(node.id != root)	dfs(node.id, idx, dst + node.w);
&amp;#125;

int main()&amp;#123;
	int p, q, d, t;
	cin &amp;gt;&amp;gt; n;
	t = n - 1;
	while(t--)&amp;#123;
		scanf(&amp;quot;%d%d%d&amp;quot;, &amp;amp;p, &amp;amp;q, &amp;amp;d);
		m[p].push_back(&amp;#123;q, d&amp;#125;);
		m[q].push_back(&amp;#123;p, d&amp;#125;);
	&amp;#125;
	dfs(1, -1, 0);
	int idx = 1;
	for(int i = 1; i &amp;lt;= n; i++)
		if(dist[i] &amp;gt; dist[idx]) idx = i;
	dfs(idx, -1, 0);
	for(int i = 1; i &amp;lt;= n; i++)
		if(dist[i] &amp;gt; dist[idx]) idx = i;
	idx = dist[idx];
	printf(&amp;quot;%lld&amp;quot;, 10 * idx + (1ll + idx) * idx / 2);	

	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;h4 id=&#34;题目&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#题目&#34;&gt;#&lt;/a&gt; 题目&lt;/h4&gt;
&lt;p&gt;字符 E 表示奶酪所在的位置，字符 # 表示墙壁，字符。表示可以通行，&lt;/p&gt;
&lt;p&gt;对于每一组数据，输出吃到奶酪的最少单位时间。&lt;/p&gt;
&lt;p&gt;若无法吃到奶酪，则输出 “oop!”（只输出引号里面的内容，不输出引号）。&lt;/p&gt;
&lt;p&gt;在 1 个单位时间内可以从当前的位置走到它上下左右四个方向上的任意一个位置，但不能走出地图边界。&lt;/p&gt;
&lt;h4 id=&#34;模板&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#模板&#34;&gt;#&lt;/a&gt; 模板&lt;/h4&gt;
