tr&gt;&lt;td data-num=&#34;41&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;		&lt;span class=&#34;token keyword&#34;&gt;if&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;tag&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt; &lt;span class=&#34;token function&#34;&gt;add&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;l&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; r&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;42&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;		&lt;span class=&#34;token keyword&#34;&gt;else&lt;/span&gt; &lt;span class=&#34;token function&#34;&gt;printf&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token string&#34;&gt;&#34;%d\n&#34;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token function&#34;&gt;query&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;r&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt; &lt;span class=&#34;token function&#34;&gt;query&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;l &lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;43&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;	&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;44&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;45&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;	&lt;span class=&#34;token keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;46&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;h4 id=&#34;线段树模板&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#线段树模板&#34;&gt;#&lt;/a&gt; 线段树模板&lt;/h4&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include&amp;lt;bits/stdc++.h&amp;gt;
using namespace std;
const int N = 100010;

int n, m;
int num[N];

struct node&amp;#123;
	int l, r;
	int sum;
&amp;#125;sgt[N * 4];		// 需要开N * 4

// 更新当前节点
void pushup(int root)&amp;#123;
	sgt[root].sum = sgt[root &amp;lt;&amp;lt; 1].sum + sgt[root &amp;lt;&amp;lt; 1 | 1].sum;
&amp;#125;

// 初始化线段树
void build(int root, int l, int r)&amp;#123;
	if(l == r) sgt[root].sum = num[l];
	else&amp;#123;
		sgt[root] = &amp;#123;l, r&amp;#125;;
		int mid = l + r &amp;gt;&amp;gt; 1;
		build(root &amp;lt;&amp;lt; 1, l, mid);
		build(root &amp;lt;&amp;lt; 1 | 1, mid + 1, r);
		pushup(root);
	&amp;#125;
	return;
&amp;#125;

int query(int root, int l, int r)&amp;#123;
	if(sgt[root].l &amp;gt;= l &amp;amp;&amp;amp; sgt[root].r &amp;lt;= r) return sgt[root].sum;
	int mid = sgt[root].l + sgt[root].r &amp;gt;&amp;gt; 1;
	int sum = 0;
	if(l &amp;lt;= mid) sum += query(root &amp;lt;&amp;lt; 1, l, r);
	if(r &amp;gt; mid) sum += query(root &amp;lt;&amp;lt; 1 | 1, l, r);
	return sum;
&amp;#125;

void modify(int root, int index, int c)&amp;#123;
	if(sgt[root].l == sgt[root].r) sgt[root].sum += c;
	else&amp;#123;
		int mid = sgt[root].l + sgt[root].r &amp;gt;&amp;gt; 1;
		if(index &amp;lt;= mid) modify(root &amp;lt;&amp;lt; 1, index, c);
		else modify(root &amp;lt;&amp;lt; 1 | 1, index, c);
		pushup(root);
	&amp;#125;
&amp;#125;

int main()&amp;#123;
	cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m;
	for(int i = 1; i &amp;lt;= n; i++) scanf(&amp;quot;%d&amp;quot;, &amp;amp;num[i]);
	build(1, 1, n);
	int k, a, b;
	while(m--)&amp;#123;
		scanf(&amp;quot;%d %d %d&amp;quot;, &amp;amp;k, &amp;amp;a, &amp;amp;b);
		if(k) modify(1, a, b);
		else printf(&amp;quot;%d\n&amp;quot;, query(1, a, b));
	&amp;#125;

	return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;p&gt;某涉密单位下发了某种票据，并要在年终全部收回。&lt;/p&gt;
&lt;p&gt;每张票据有唯一的 ID 号。&lt;/p&gt;
&lt;p&gt;全年所有票据的 ID 号是连续的，但 ID 的开始数码是随机选定的。&lt;/p&gt;
&lt;p&gt;因为工作人员疏忽，在录入 ID 号的时候发生了一处错误，造成了某个 ID 断号，另外一个 ID 重号。&lt;/p&gt;
&lt;p&gt;你的任务是通过编程，找出断号的 ID 和重号的 ID。&lt;/p&gt;
&lt;p&gt;假设断号不可能发生在最大和最小号。&lt;/p&gt;
&lt;h4 id=&#34;输入格式&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#输入格式&#34;&gt;#&lt;/a&gt; 输入格式&lt;/h4&gt;
&lt;p&gt;第一行包含整数 &lt;strong&gt;N&lt;/strong&gt;，表示后面共有 &lt;strong&gt;N&lt;/strong&gt; 行数据。&lt;/p&gt;
&lt;p&gt;接下来 &lt;strong&gt;N&lt;/strong&gt; 行，每行包含空格分开的若干个（不大于 100 个）正整数（不大于 100000），每个整数代表一个 ID 号。&lt;/p&gt;
&lt;h4 id=&#34;输出格式&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#输出格式&#34;&gt;#&lt;/a&gt; 输出格式&lt;/h4&gt;
&lt;p&gt;要求程序输出 1 行，含两个整数 &lt;strong&gt;m&lt;/strong&gt;,&lt;strong&gt;n&lt;/strong&gt; 用空格分隔。&lt;/p&gt;
&lt;p&gt;其中，&lt;strong&gt;m&lt;/strong&gt; 表示断号 ID，&lt;strong&gt;n&lt;/strong&gt; 表示重号 ID。&lt;/p&gt;
&lt;h4 id=&#34;数据范围&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#数据范围&#34;&gt;#&lt;/a&gt; 数据范围&lt;/h4&gt;
&lt;p&gt;&lt;strong&gt;1&lt;/strong&gt;≤&lt;strong&gt;N&lt;/strong&gt;≤&lt;strong&gt;100&lt;/strong&gt;&lt;/p&gt;
&lt;h4 id=&#34;输入样例&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#输入样例&#34;&gt;#&lt;/a&gt; 输入样例：&lt;/h4&gt;
&lt;pre&gt;&lt;code&gt;2
5 6 8 11 9 
10 12 9
&lt;/code&gt;&lt;/pre&gt;
&lt;h4 id=&#34;输出样例&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#输出样例&#34;&gt;#&lt;/a&gt; 输出样例：&lt;/h4&gt;
&lt;pre&gt;&lt;code&gt;7 9
&lt;/code&gt;&lt;/pre&gt;
&lt;h4 id=&#34;ac代码&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#ac代码&#34;&gt;#&lt;/a&gt; AC 代码&lt;/h4&gt;
