 \begin{align*}
\begin{split}
g(i,j)= \left \{
\begin{array}{ll}
\alpha f(i,j),&amp;0\le f(i,j) &lt; a\\
\beta (f(i,j)-a)+g_{a},&amp;a\le f(i,j) &lt; b\\
\gamma  (f(i,j)-b)+g_{b},&amp;b\le f(i,j) &lt; 255
\end{array}
\right.
\end{split}
\end{align*}

